from aiogram import types
from aiogram.dispatcher import FSMContext  # –°–æ—Å—Ç–æ—è–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
from loguru import logger

from keyboards.user_keyboards import services_and_prices_key
from system.dispatcher import bot, dp


@dp.callback_query_handler(lambda c: c.data == "services_and_prices")
async def services_and_prices(callback_query: types.CallbackQuery, state: FSMContext):
    try:
        await state.finish()  # –ó–∞–≤–µ—Ä—à–∞–µ–º —Ç–µ–∫—É—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π
        await state.reset_state()  # –°–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—Å–µ –¥–∞–Ω–Ω—ã–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π, –¥–æ –∑–Ω–∞—á–µ–Ω–∏—è –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
        from_user_name = callback_query.from_user.first_name  # –ü–æ–ª—É—á–∞–µ–º —Ñ–∞–º–∏–ª–∏—é –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        greeting_message = f"{from_user_name}, –æ–∑–Ω–∞–∫–æ–º—å—Ç–µ—Å—å —Å –Ω–∞—à–∏–º–∏ —É—Å–ª—É–≥–∞–º–∏ –∏ —Ü–µ–Ω–∞–º–∏!\n\n"
        services_and_prices_keyboard = services_and_prices_key()  # –ö–ª–∞–≤–∏–∞—Ç—É—Ä—ã –ø–æ—Å—Ç–∞ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è üëã
        await bot.send_message(callback_query.from_user.id,  # ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
                               text=greeting_message,  # –¢–µ–∫—Å—Ç –¥–ª—è –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è üëã
                               reply_markup=services_and_prices_keyboard,  # –ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è üëã
                               parse_mode=types.ParseMode.HTML)  # –¢–µ–∫—Å—Ç –≤ HTML-—Ä–∞–∑–º–µ—Ç–∫–∏
    except Exception as error:
        logger.exception(error)

@dp.callback_query_handler(lambda c: c.data == "cargo_delivery_prices")
async def cargo_delivery_prices(callback_query: types.CallbackQuery, state: FSMContext):
    try:
        await state.finish()  # –ó–∞–≤–µ—Ä—à–∞–µ–º —Ç–µ–∫—É—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π
        await state.reset_state()  # –°–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—Å–µ –¥–∞–Ω–Ω—ã–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π, –¥–æ –∑–Ω–∞—á–µ–Ω–∏—è –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
        greeting_message = f"–ø–æ—Å–ª–µ –Ω–∞–∂–∞—Ç–∏—è —Å–ª–µ–¥—É–µ—Ç —Å–æ–æ–±—â–µ–Ω–∏–µ —Å —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏—è–º–∏ –ø—Ä–∞–π—Å-–ª–∏—Å—Ç–æ–≤. –ò –ø–æ–¥–ø–∏—Å—å: –ú–∏–Ω–∏–º–∞–ª—å–Ω—ã–π –≤–µ—Å –Ω–∞ –≥—Ä—É–∑–æ–ø–µ—Ä–µ–≤–æ–∑–∫—É 20 –∫–≥.\n\n"
        await bot.send_message(callback_query.from_user.id,  # ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
                               text=greeting_message,  # –¢–µ–∫—Å—Ç –¥–ª—è –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è üëã
                               parse_mode=types.ParseMode.HTML)  # –¢–µ–∫—Å—Ç –≤ HTML-—Ä–∞–∑–º–µ—Ç–∫–∏
    except Exception as error:
        logger.exception(error)

@dp.callback_query_handler(lambda c: c.data == "white_cargo_delivery_with_gas_turbine_engine")
async def white_cargo_delivery_with_gas_turbine_engine(callback_query: types.CallbackQuery, state: FSMContext):
    try:
        await state.finish()  # –ó–∞–≤–µ—Ä—à–∞–µ–º —Ç–µ–∫—É—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π
        await state.reset_state()  # –°–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—Å–µ –¥–∞–Ω–Ω—ã–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π, –¥–æ –∑–Ω–∞—á–µ–Ω–∏—è –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
        greeting_message = (f"–ü–æ–º–æ–∂–µ–º –ø–µ—Ä–µ–≤–µ–∑—Ç–∏ –≥—Ä—É–∑ –∏ –ø—Ä–æ–π—Ç–∏ —Ç–∞–º–æ–∂–Ω—é –±–µ–∑ –ø–æ—Ç–µ—Ä—å –≤—Ä–µ–º–µ–Ω–∏ –∏ –¥–µ–Ω–µ–≥. –°–¥–µ–ª–∞–µ–º –∫–æ–º–ø–ª–µ–∫—Å–Ω—É—é –ø–æ—Å—Ç–∞–≤–∫—É: –ø–æ–¥–±–µ—Ä–µ–º —Ç—Ä–∞–Ω—Å–ø–æ—Ä—Ç, —Å–æ–±–µ—Ä–µ–º –¥–æ–∫—É–º–µ–Ω—Ç—ã, –ø–æ–¥–∞–¥–∏–º –¥–µ–∫–ª–∞—Ä–∞—Ü–∏—é, –æ—Ñ–æ—Ä–º–∏–º –∏ –ø—Ä–∏–≤–µ–∑–µ–º –≥—Ä—É–∑ –Ω–∞ —Å–∫–ª–∞–¥. –ò–ª–∏ –ø–æ–¥–∫–ª—é—á–∏–º—Å—è –Ω–∞ –Ω—É–∂–Ω–æ–º —ç—Ç–∞–ø–µ.\n\n"
                            f"‚Ä¢ –ü–æ–∏—Å–∫ –ø—Ä–æ–∏–∑–≤–æ–¥—Å—Ç–≤–∞\n"
                            f"‚Ä¢ –í—ã–∫—É–ø —Ç–æ–≤–∞—Ä–∞\n"
                            f"‚Ä¢ –õ–æ–≥–∏—Å—Ç–∏–∫–∞\n"
                            f"‚Ä¢ –¢–∞–º–æ–∂–µ–Ω–Ω–æ–µ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏–µ, —Ä–∞–∑—Ä–µ—à–∏—Ç–µ–ª—å–Ω—ã–µ –¥–æ–∫—É–º–µ–Ω—Ç—ã\n"
                            f"–ó–∞–¥–∞—Ç—å –≤–æ–ø—Ä–æ—Å –∏–ª–∏ –ø–µ—Ä–µ–¥–∞—Ç—å –∑–∞—è–≤–∫—É –Ω–∞ —Ä–∞—Å—á—ë—Ç —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç—É –í–≠–î : –í –¥–∞–Ω–Ω—ã–π –º–æ–º–µ–Ω—Ç —É—Å–ª—É–≥–∞ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–∞!)\n\n")
        await bot.send_message(callback_query.from_user.id,  # ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
                               text=greeting_message,  # –¢–µ–∫—Å—Ç –¥–ª—è –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è üëã
                               # reply_markup=services_and_prices_keyboard,  # –ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è üëã
                               parse_mode=types.ParseMode.HTML)  # –¢–µ–∫—Å—Ç –≤ HTML-—Ä–∞–∑–º–µ—Ç–∫–∏
    except Exception as error:
        logger.exception(error)

@dp.callback_query_handler(lambda c: c.data == "goods_redemption_service")
async def goods_redemption_service(callback_query: types.CallbackQuery, state: FSMContext):
    try:
        await state.finish()  # –ó–∞–≤–µ—Ä—à–∞–µ–º —Ç–µ–∫—É—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π
        await state.reset_state()  # –°–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—Å–µ –¥–∞–Ω–Ω—ã–µ –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π, –¥–æ –∑–Ω–∞—á–µ–Ω–∏—è –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
        greeting_message = (f"–†–∞–±–æ—Ç–∞–µ–º —Ç–æ–ª—å–∫–æ –æ—Ç 20 –∫–≥!\n\n"
                            f"‚úÖ –í—ã–∫—É–ø —Ç–æ–≤–∞—Ä–∞ (—É—Å–ª–æ–≤–∏—è –∏ –∫–æ–º–∏—Å—Å–∏–∏)\n"
                            f"–í–∞–º –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –ø—Ä–µ–¥–æ—Å—Ç–∞–≤–∏—Ç—å —Å—Å—ã–ª–∫—É –Ω–∞ –ø–æ—Å—Ç–∞–≤—â–∏–∫–∞, —Ç–æ–≤–∞—Ä —Å —Ñ–æ—Ç–æ –∏ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∞–º–∏, –ø—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ–º–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ.\n"
                            f"–ß—Ç–æ –¥–∞–ª—å—à–µ?\n"
                            f"‚Ä¢	–ú—ã —Ñ–æ—Ä–º–∏—Ä—É–µ–º –∫–æ—Ä–∑–∏–Ω—É –ø–æ –≤–∞—à–µ–π –∑–∞—è–≤–∫–µ, –ø–µ—Ä–µ–¥–∞—ë–º –≤–∞–º –Ω–∞ –ø—Ä–æ–≤–µ—Ä–∫—É —Å–æ–±—Ä–∞–Ω–Ω—É—é –∑–∞—è–≤–∫—É, –í—ã —Å–æ–≥–ª–∞—Å–æ–≤—ã–≤–∞–µ—Ç–µ –≤—ã–∫—É–ø, –æ–ø–ª–∞—á–∏–≤–∞–µ—Ç–µ –Ω–µ–æ–±—Ö–æ–¥–∏–º—É—é —Å—É–º–º—É, –º—ã –≤—ã–∫—É–ø–∞–µ–º!\n\n"
                            f"–ö–æ–º–∏—Å—Å–∏—è –∑–∞ –≤—ã–∫—É–ø —Ç–æ–≤–∞—Ä–∞ —Å–æ—Å—Ç–∞–≤–∏—Ç:\n"
                            f"‚Ä¢	–ó–∞–∫—É–ø –¥–æ 3.000 ¬• - –∫–æ–º–∏—Å—Å–∏—è 8%\n"
                            f"‚Ä¢	–ó–∞–∫—É–ø 3.000 - 5.000 ¬• - –∫–æ–º–∏—Å—Å–∏—è 5%\n"
                            f"‚Ä¢	–ó–∞–∫—É–ø 5.000 - 10.000 ¬• - –∫–æ–º–∏—Å—Å–∏—è 4%\n"
                            f"‚Ä¢	–ó–∞–∫—É–ø 10.000 - 50.000 ¬• - –∫–æ–º–∏—Å—Å–∏—è 3%\n"
                            f"‚Ä¢	–ó–∞–∫—É–ø —Å–≤—ã—à–µ 50.000 ¬• - –∫–æ–º–∏—Å—Å–∏—è 2%\n\n"
                            f"–ú–∏–Ω–∏–º–∞–ª—å–Ω–∞—è —Å—É–º–º–∞ –Ω–∞ –≤—ã–∫—É–ø 1000¬•, –º–∏–Ω–∏–º–∞–ª—å–Ω—ã–π –≤–µ—Å –≥—Ä—É–∑–∞ –Ω–∞ –¥–æ—Å—Ç–∞–≤–∫—É —á–µ—Ä–µ–∑ –Ω–∞—Å 20 –∫–≥.\n\n"
                            f"–ß—Ç–æ –≤—Ö–æ–¥–∏—Ç –≤ –∫–æ–º–∏—Å—Å–∏—é:\n"
                            f"–§–æ—Ä–º–∏—Ä–æ–≤–∞–Ω–∏–µ, —Ä–∞–∑–º–µ—â–µ–Ω–∏–µ –∏ –æ–ø–ª–∞—Ç–∞ –∑–∞–∫–∞–∑–∞, –æ–±—â–µ–Ω–∏–µ —Å –ø–æ—Å—Ç–∞–≤—â–∏–∫–æ–º –µ—Å–ª–∏ –µ—Å—Ç—å –∫–∞–∫–∏–µ-—Ç–æ —Ç—Ä—É–¥–Ω–æ—Å—Ç–∏ —Å –∑–∞–∫–∞–∑–æ–º, —Å–æ–≥–ª–∞—Å–æ–≤—ã–≤–∞–µ–º –æ—Å—Ç–∞—Ç–∫–∏ —Å –ø–æ—Å—Ç–∞–≤—â–∏–∫–æ–º (–µ—Å–ª–∏ –ø–æ—Å—Ç–∞–≤—â–∏–∫ –Ω–µ –º–æ–∂–µ—Ç –≤—ã–ø–æ–ª–Ω–∏—Ç—å –∑–∞–∫–∞–∑, —Ñ–æ—Ä–º–∏—Ä—É–µ–º –∑–∞—è–≤–∫—É –Ω–∞ –≤–æ–∑–≤—Ä–∞—Ç), –¥–µ–ª–∞–µ–º —Ñ–æ—Ç–æ –∏ –≤–∏–¥–µ–æ (–∏–∑ –æ–¥–Ω–æ–π –∫–æ—Ä–æ–±–∫–∏ - 1 –µ–¥–∏–Ω–∏—Ü–∞ —Ç–æ–≤–∞—Ä–∞), —Ö—Ä–∞–Ω–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞ –Ω–∞ —Å–∫–ª–∞–¥–µ –¥–æ –º–æ–º–µ–Ω—Ç–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏.\n\n"
                            f"‚úÖ –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è —É—Å–ª—É–≥–∞: –Ω–∞–∫–ª–µ–π–∫–∏ —Å–æ —à—Ç—Ä–∏—Ö –∫–æ–¥–∞–º–∏ –Ω–∞ —Ç–æ–≤–∞—Ä –¥–ª—è –º–∞—Ä–∫–µ—Ç–ø–ª–µ–π—Å–æ–≤ –æ—Ç 100-300 —à—Ç 1 —é–∞–Ω—å (–ø–æ –∫—É—Ä—Å—É).\n"
                            f"‚úÖ –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è —É—Å–ª—É–≥–∞: –ø—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ —Ç–æ–≤–∞—Ä–∞ –Ω–∞ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ, –µ—Å–ª–∏ –≤—ã–∫—É–ø–∞–µ—Ç–µ —Å–∞–º–∏ 1-2 —é–∞–Ω—è –∑–∞ 1 —Å—á–µ—Ç–Ω—É—é –µ–¥–∏–Ω–∏—Ü—É (–∑–∞–≤–∏—Å–∏—Ç –æ—Ç —É–ø–∞–∫–æ–≤–∫–∏ —Ç–æ–≤–∞—Ä–∞).\n\n"
                            f"–ï—Å–ª–∏ –≤—ã–∫—É–ø–∞–µ–º –ú—ã 0.5 - 1 —é–∞–Ω—å (–∑–∞–≤–∏—Å–∏—Ç –æ—Ç —É–ø–∞–∫–æ–≤–∫–∏ —Ç–æ–≤–∞—Ä–∞)\n"
                            f"‚ùóÔ∏è –ú—ã –æ—Ç–≤–µ—á–∞–µ–º –∑–∞:\n"
                            f"* –í—ã–∫—É–ø —Ç–æ–≤–∞—Ä–∞ —Å–æ–≥–ª–∞—Å–Ω–æ –∑–∞—è–≤–∫–µ\n"
                            f"* –î–æ—Å—Ç–∞–≤–∫—É –≥—Ä—É–∑–∞ –ø–æ –≤—Å–µ–º —Ç—Ä–µ–∫-–Ω–æ–º–µ—Ä–∞–º –Ω–∞ –Ω–∞—à —Å–∫–ª–∞–¥ –≤ –ö–∏—Ç–∞–µ\n"
                            f"* –ö–æ–Ω—Å–æ–ª–∏–¥–∞—Ü–∏—é –≥—Ä—É–∑–∞\n"
                            f"* –£–ø–∞–∫–æ–≤–∫—É –≥—Ä—É–∑–∞ —Å–æ–≥–ª–∞—Å–Ω–æ —Ç—Ä–µ–±–æ–≤–∞–Ω–∏—è–º\n"
                            f"* –î–æ—Å—Ç–∞–≤–∫—É –≥—Ä—É–∑–∞ –≤ –†–§ (–≥. –ú–æ—Å–∫–≤–∞)\n\n"
                            f"‚ùóÔ∏è –†–∞–±–æ—Ç–∞—è —Å –≤–∞—à–∏–º –ø–æ—Å—Ç–∞–≤—â–∏–∫–æ–º –º—ã –Ω–µ –Ω–µ—Å–µ–º –æ—Ç–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω–æ—Å—Ç–∏ –∑–∞:\n"
                            f"* –ö–∞—á–µ—Å—Ç–≤–æ –µ–≥–æ –ø—Ä–æ–¥—É–∫—Ü–∏–∏\n"
                            f"* –°—Ä–æ–∫ –æ–±—Ä–∞–±–æ—Ç–∫–∏, –∏–∑–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è –∏ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ç–æ–≤–∞—Ä–∞ –ø–æ—Å—Ç–∞–≤—â–∏–∫–∞–º–∏ –Ω–∞ –Ω–∞—à —Å–∫–ª–∞–¥ –≤ –ö–∏—Ç–∞–µ\n"
                            f"* –ë—Ä–∞–∫–æ–≤–∞–Ω–Ω—ã–π —Ç–æ–≤–∞—Ä - –∫–æ–º–ø–∞–Ω–∏—è –Ω–µ –ø—Ä–æ–≤–µ—Ä—è–µ—Ç —Ç–æ–≤–∞—Ä –Ω–∞ –Ω–∞–ª–∏—á–∏–µ —è–≤–Ω—ã—Ö –∏ —Å–∫—Ä—ã—Ç—ã—Ö –¥–µ—Ñ–µ–∫—Ç–æ–≤ (–î–∞–Ω–Ω—É—é —É—Å–ª—É–≥—É –º–æ–∂–Ω–æ –∑–∞–ø—Ä–æ—Å–∏—Ç—å, –æ–ø–ª–∞—á–∏–≤–∞–µ—Ç—Å—è –æ—Ç–¥–µ–ª—å–Ω–æ).\n"
                            f"* –¶–≤–µ—Ç —Ç–æ–≤–∞—Ä–∞ - —Ü–≤–µ—Ç–æ–ø–µ—Ä–µ–¥–∞—á–∞ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏ –º–æ–∂–µ—Ç –æ—Ç–ª–∏—á–∞—Ç—å—Å—è –æ—Ç —Ä–µ–∞–ª—å–Ω–æ–≥–æ —Ü–≤–µ—Ç–∞ —Ç–æ–≤–∞—Ä–∞. \n"
                            f"* –û—Ç–ø—Ä–∞–≤–∫—É –ø–æ—Å—Ç–∞–≤—â–∏–∫–æ–º –Ω–µ —Ç–æ–≥–æ —Ç–æ–≤–∞—Ä–∞, –∫–æ—Ç–æ—Ä—ã–π –±—ã–ª –∑–∞–∫–∞–∑–∞–Ω (–µ—Å–ª–∏ –Ω–µ –∑–∞–∫–∞–∑–∞–Ω–∞ —É—Å–ª—É–≥–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏—è)\n"
                            f"* –û—Ç–ø—Ä–∞–≤–∫—É –ø–æ—Å—Ç–∞–≤—â–∏–∫–æ–º —Ç–æ–≤–∞—Ä–∞ –¥—Ä—É–≥–æ–≥–æ —Ä–∞–∑–º–µ—Ä–∞\n")
        await bot.send_message(callback_query.from_user.id,  # ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
                               text=greeting_message,  # –¢–µ–∫—Å—Ç –¥–ª—è –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è üëã
                               parse_mode=types.ParseMode.HTML)  # –¢–µ–∫—Å—Ç –≤ HTML-—Ä–∞–∑–º–µ—Ç–∫–∏
    except Exception as error:
        logger.exception(error)

def register_services_and_prices_handler():
    """–†–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–µ–º handlers –¥–ª—è –±–æ—Ç–∞"""
    dp.register_message_handler(services_and_prices)
    dp.register_message_handler(cargo_delivery_prices)
    dp.register_message_handler(white_cargo_delivery_with_gas_turbine_engine)
    dp.register_message_handler(goods_redemption_service)
